// This is the Example of Widening Type Casting 
public class WideningTypeEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=7;
		long y=x;
		float z=y;
		
		System.out.println("Before Converting "+x);
		System.out.println("After Converting in Long "+y);
		System.out.println("After Converting in Float "+z);
	}

}
